const singInButton=document.querySelector(".buttons__button-prime"),modal=document.querySelector(".modal-wrapper");async function submitForm(t){t.preventDefault();try{const e=await fetch(t.target.action,{method:"POST",body:new FormData(t.target)});if(!e.ok)throw`Ошибка при обращении к серверу: ${e.status}`;const a=e.headers.get("content-type");if(!a||!a.includes("application/json"))throw"Не отправлено. Вы - РОБОТ!";const o=await e.json();if("success"!==o.result)throw console.log(o),o.info;alert(o.info),setTimeout((()=>{modal.classList.remove("modal-wrapper_active")}),"2000")}catch(t){alert(t)}}singInButton.addEventListener("click",(()=>{modal.classList.add("modal-wrapper_active")})),modal.addEventListener("click",(t=>{t.target.classList.contains("modal-wrapper_active")&&modal.classList.remove("modal-wrapper_active")}));